import axios from 'axios';

const API_URL = 'http://localhost/e-library-web-app/backend/api';

export const bookService = {
    async getBooks() {
        const response = await fetch(`${API_URL}/books.php`);
        return await response.json();
    },
    
    addBook: async (bookData) => {
        try {
            const response = await axios.post(`${API_URL}/addBook.php`, {
                title: bookData.title,
                author: bookData.author,
                description: bookData.description,
                pdf_url: bookData.pdfUrl  // Changed to match database field name
            }, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            
            if (!response.data) {
                throw new Error('No response from server');
            }
            
            return response.data;
        } catch (error) {
            console.error('Add book error:', error.response || error);
            throw new Error(error.response?.data?.message || 'Server error');
        }
    }
};
