import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Alert, Spinner } from 'react-bootstrap';
import { useLocation } from 'react-router-dom';
import Pagination from 'react-bootstrap/Pagination';
import Form from 'react-bootstrap/Form';

function Books() {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [categories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 10,
    total: 0,
    totalPages: 0
  });
  const [searchTerm, setSearchTerm] = useState('');
  const location = useLocation();

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const search = searchParams.get('search');
    setSearchTerm(search || '');
    fetchBooks(search, pagination.page);
  }, [location.search]);

  const fetchBooks = async (search, page = 1) => {
    setLoading(true);
    setError(null);
    
    try {
      const baseUrl = 'http://localhost/e-library-web-app/backend/api';
      let url = `${baseUrl}/books.php?page=${page}&limit=${pagination.limit}`;
      
      if (search) {
        url += `&title=${encodeURIComponent(search)}`; // Changed to search by title
      }
      
      if (selectedCategory) {
        url += `&category=${selectedCategory}`;
      }

      const response = await fetch(url);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      
      const data = await response.json();
      
      if (data.status === 'error') {
        throw new Error(data.message);
      }
      
      setBooks(data.books || []);
      setPagination({
        ...pagination,
        page: data.page || 1,
        totalPages: data.total_pages || 1,
        total: data.total || 0
      });
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const renderPagination = () => {
    const pages = [];
    for (let i = 1; i <= pagination.totalPages; i++) {
      pages.push(
        <Pagination.Item
          key={i}
          active={i === pagination.page}
          onClick={() => fetchBooks(searchTerm, i)}
        >
          {i}
        </Pagination.Item>
      );
    }
    return pages;
  };

  if (loading) {
    return (
      <Container className="mt-4 text-center">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Loading...</span>
        </Spinner>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="mt-4">
        <Alert variant="danger">
          Error: {error}
        </Alert>
      </Container>
    );
  }

  return (
    <Container className="mt-4">
      <Form.Select 
        className="mb-3"
        value={selectedCategory}
        onChange={(e) => setSelectedCategory(e.target.value)}
      >
        <option value="">All Categories</option>
        {categories.map(cat => (
          <option key={cat.id} value={cat.id}>{cat.name}</option>
        ))}
      </Form.Select>

      {books.length === 0 ? (
        <Alert variant="info">No books found</Alert>
      ) : (
        <Row>
          {books.map((book) => (
            <Col key={book.id} md={4} className="mb-4">
              <Card>
                <Card.Body>
                  <Card.Title>{book.title}</Card.Title>
                  <Card.Text>
                    <strong>Author:</strong> {book.author}<br />
                    <strong>ISBN:</strong> {book.isbn}
                  </Card.Text>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}

      <Pagination className="mt-3 justify-content-center">
        <Pagination.First 
          onClick={() => fetchBooks(searchTerm, 1)}
          disabled={pagination.page === 1}
        />
        <Pagination.Prev 
          onClick={() => fetchBooks(searchTerm, pagination.page - 1)}
          disabled={pagination.page === 1}
        />
        {renderPagination()}
        <Pagination.Next 
          onClick={() => fetchBooks(searchTerm, pagination.page + 1)}
          disabled={pagination.page === pagination.totalPages}
        />
        <Pagination.Last 
          onClick={() => fetchBooks(searchTerm, pagination.totalPages)}
          disabled={pagination.page === pagination.totalPages}
        />
      </Pagination>
    </Container>
  );
}

export default Books;
