import axios from 'axios';

const API_URL = 'http://localhost/e-library-web-app/backend/api';

export const bookService = {
    async getBooks() {
        const response = await fetch(`${API_URL}/books.php`);
        return await response.json();
    },
    
    addBook: async (bookData) => {
        try {
            const response = await axios.post(`${API_URL}/addBook.php`, {
                title: bookData.title,
                author: bookData.author,
                description: bookData.description,
                pdfUrl: bookData.pdfUrl
            }, {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            });
            
            if (response.data.message) {
                if (response.status === 201) {
                    return response.data;
                } else {
                    throw new Error(response.data.message);
                }
            }
            throw new Error('Invalid response from server');
        } catch (error) {
            console.error('Error details:', error);
            throw new Error(error.response?.data?.message || 'Failed to add book. Please try again.');
        }
    }
};
