import React, { useState } from 'react';
import { Form, Button, Container, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { bookService } from '../services/bookService';

function AddBook() {
    const [book, setBook] = useState({ title: '', author: '', description: '', pdfUrl: '' });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        // Validate URL format
        try {
            new URL(book.pdfUrl);
        } catch (e) {
            setError('Please enter a valid URL');
            setLoading(false);
            return;
        }

        try {
            await bookService.addBook(book);
            navigate('/books');
        } catch (error) {
            setError(error.message || 'Failed to add book. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container className="mt-4">
            <h2>Add New Book</h2>
            {error && <Alert variant="danger">{error}</Alert>}
            <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                    <Form.Label>Title</Form.Label>
                    <Form.Control
                        type="text"
                        value={book.title}
                        onChange={(e) => setBook({...book, title: e.target.value})}
                        required
                    />
                </Form.Group>
                <Form.Group className="mb-3">
                    <Form.Label>Author</Form.Label>
                    <Form.Control
                        type="text"
                        value={book.author}
                        onChange={(e) => setBook({...book, author: e.target.value})}
                        required
                    />
                </Form.Group>
                <Form.Group className="mb-3">
                    <Form.Label>Description</Form.Label>
                    <Form.Control
                        as="textarea"
                        value={book.description}
                        onChange={(e) => setBook({...book, description: e.target.value})}
                        required
                    />
                </Form.Group>
                <Form.Group className="mb-3">
                    <Form.Label>PDF URL</Form.Label>
                    <Form.Control
                        type="url"
                        placeholder="https://example.com/book.pdf"
                        value={book.pdfUrl}
                        onChange={(e) => {
                            setError('');
                            setBook({...book, pdfUrl: e.target.value});
                        }}
                        required
                    />
                    <Form.Text className="text-muted">
                        Enter a valid PDF URL (must start with http:// or https://)
                    </Form.Text>
                </Form.Group>
                <Button 
                    type="submit" 
                    disabled={loading}
                    className="w-100"
                >
                    {loading ? 'Adding...' : 'Add Book'}
                </Button>
            </Form>
        </Container>
    );
}

export default AddBook;
