import axios from 'axios';

const API_URL = 'http://localhost/e-library-web-app/backend/api';

export const bookService = {
    async getBooks() {
        const response = await fetch(`${API_URL}/books.php`);
        return await response.json();
    },
    
    addBook: async (bookData) => {
        try {
            const response = await axios.post(`${API_URL}/addBook`, {
                title: bookData.title,
                author: bookData.author,
                description: bookData.description,
                pdfUrl: bookData.pdfUrl
            }, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            return response.data;
        } catch (error) {
            throw error.response?.data?.message || 'Error adding book';
        }
    }
};
